package week1;

public class j_7115_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 5;
		int n2 = 2;
		
		System.out.println("n1+n2:"+n1+n2);
		System.out.println("n1-n2:"+(n1-n2));
		System.out.println("n1/n2:"+n1/n2);
		System.out.println("n1%n2:"+n1%n2);
		System.out.println("n1*n2:"+n1*n2);
		
	}
}